import React from 'react';
import { Slide } from 'react-slideshow-image';
import './home.css'; // Import your custom CSS file for styling

const HomePage = () => {
    const slideImages = [
        'https://example.com/image1.jpg',
        'https://example.com/image2.jpg',
        'https://example.com/image3.jpg'
    ];

    return (
        <div className="home-container">
            <div className="content-section">
                <div className="full-screen-info">
                    <h2>HRM VISAS</h2>
                    <p>We have been in the field of Visa Services since 2008, gaining extensive experience by processing thousands of Visa applications. Our company's mission is to provide the best possible support and services to our customers, making a positive impact on their lives.</p>
                    <p>We specialize in Visas, Travel, Flight, Hotel, Insurance, and Documentation to make your journey hassle-free.</p>
                </div>
            </div>

            <div className="slideshow-container">
                <Slide>
                    {slideImages.map((each, index) => (
                        <div key={index} className="each-slide">
                            <img src={each} alt={`Slide ${index}`} />
                        </div>
                    ))}
                </Slide>
            </div>

            <div className="content-section">
                <div className="additional-info">
                    <h4>About</h4>
                    <p>Our company has been established with the mission to provide the best possible and continuous support in our customer and services to make a positive impact on their lives. We strive to make the visa application process easier and more convenient for our clients.</p>
                </div>
            </div>
        </div>
    );
}

export default HomePage;
